int[0, 1073741824] approxCircNodes = 0;
int[0, 1073741824] approxCircGates = 0;
int approxCircDly=0;
int[0, 1073741824] approxCircDepth=0;


bool outEq(){
    bool same=true;
    int i;
    hamming_distance = 0;
    for(i=0; i<NPO; i++){
        if((POx[i]>=0) && (POy[i]>=0)){
            if(bits[POx[i]] != bits[POy[i]]){
                hamming_distance++;
                same = false;
            }
        }
    }

    if (same == false) {
        dif_count = dif_count + 1;
    }

    if (hamming_distance > max_hamming_distance) {
        max_hamming_distance = hamming_distance;
    }

    res_count = res_count + 1;

    return same;
}

double EP() {
    //error probability
    //number of different outputs divided by number of results
    double ret = dif_count / res_count;

    return ret;
}

double MAE() {
    //mean absolute error
    //sum of differences (approx. vs acc.) divided by number of results
    double ret = out_sum_all_diff / res_count;

    return ret;
}

double MSE() {
    //mean squared error
    //sum of squared differences divided by number of results
    double ret = out_sum_all_diff_squared / res_count;

    return ret;
}

double MRE() {
    //mean relative error
    //mean error in relation to size of accurate output
    double ret = out_sum_relative / res_count;

    return ret;
}

double avg_bitflips() {
    //total number of bit flips divided by number of results
    double ret = bit_flips_sum / res_count;

    return ret;
}

void calc_metrics() {
    error_prob = EP();
    mean_abs_error = MAE();
    mean_squared_error = MSE();
    mean_relative_error = MRE();
    avg_flips_per_res = avg_bitflips();
}

int[0, MAX_INT] res_acc=0;
int[0, MAX_INT] res_approx=0;

void diff(){
    int i;
    double res_dif;
    double relative_res_dif;
    res_acc = 0;
    res_approx = 0;

    difference = !outEq();

     for(i=0; i<=NPO-1; i++){                                       
        res_acc += bits[i+NPI]*TBL_PWR2[i];
        if(POy[i]>=0) res_approx += bits[i+NPI+NPO]*TBL_PWR2[i];    
    }
    out_sum_all_acc = out_sum_all_acc + res_acc;
    out_sum_all_approx = out_sum_all_approx + res_approx;

    out_sum_all_diff = fabs(out_sum_all_acc - out_sum_all_approx);
    out_sum_all_diff_squared = out_sum_all_diff_squared + pow(abs(res_approx-res_acc),2);
    out_sum_relative = out_sum_relative + (abs(res_approx-res_acc) / fmax(1.0, 1.0*res_acc));

    res_dif = 1.0 * abs(res_approx - res_acc);
    if (res_dif > worst_case_error) {
        worst_case_error = res_dif;
    }

    relative_res_dif = abs(res_approx - res_acc) / fmax(1.0, 1.0*res_acc);
    if (relative_res_dif > worst_case_relative_error) {
        worst_case_relative_error = relative_res_dif;
    }

    err_magn_v = res_acc - res_approx;
    if(res_acc!=0) err_magn_r = 1-((1.0*res_approx)/(1.0*res_acc));

    if(max_bit_flips < bit_flips) {
        max_bit_flips = bit_flips;
    }

    bit_flips = 0;

    calc_metrics();
}

template.declaration:
clock x;

int getIdx(int i){
    int idx=-1;

    if(i < NPI) {
        idx = PIxy[i];
    }
    else {
        idx = POx[i-NPI];
    }

    return idx;
}


int[0, MAX_INT] bin2dec()
{
    int[0, MAX_INT] result=0;
    int i;
    for(i=0; i<NIB_MUL2; i++){
        result += bits[getIdx(i)]*TBL_PWR2[i];
    }

    return result;
}


int getOut(){
    int res, i;
    for(i=NIB_MUL2; i<NIB_MUL2+NOB_MUL2; i++){
        res += bits[getIdx(i)]*TBL_PWR2[i-NIB_MUL2];
    }
    return res;    
}

void f(){
    if(POx[15]>=0) bits[POx[15]]=ttbl[bin2dec()][16];
    if(POx[14]>=0) bits[POx[14]]=ttbl[bin2dec()][17];
    if(POx[13]>=0) bits[POx[13]]=ttbl[bin2dec()][18];
    if(POx[12]>=0) bits[POx[12]]=ttbl[bin2dec()][19];
    if(POx[11]>=0) bits[POx[11]]=ttbl[bin2dec()][20];
    if(POx[10]>=0) bits[POx[10]]=ttbl[bin2dec()][21];
    if(POx[9]>=0) bits[POx[9]]=ttbl[bin2dec()][22];
    if(POx[8]>=0) bits[POx[8]]=ttbl[bin2dec()][23];
    if(POx[7]>=0) bits[POx[7]]=ttbl[bin2dec()][24];
    if(POx[6]>=0) bits[POx[6]]=ttbl[bin2dec()][25];
    if(POx[5]>=0) bits[POx[5]]=ttbl[bin2dec()][26];
    if(POx[4]>=0) bits[POx[4]]=ttbl[bin2dec()][27];
    if(POx[3]>=0) bits[POx[3]]=ttbl[bin2dec()][28];
    if(POx[2]>=0) bits[POx[2]]=ttbl[bin2dec()][29];
    if(POx[1]>=0) bits[POx[1]]=ttbl[bin2dec()][30];
    if(POx[0]>=0) bits[POx[0]]=ttbl[bin2dec()][31];

    diffctrl++;
}

