void dec_to_bin(int number, bool &number_binary[NPI/2]) {
    //init all bits to 0
    int j;
    int index;
    for(i:int[0,(NPI/2 - 1)]) {
        number_binary[i] = 0;
    }

    if(number == 0) { 
        return; 
    }

    while(number > 0) {
        index = NPI/2 - 1 - j;
        if(index < 0) index = 0;
        number_binary[index] = number % 2;
        number /= 2;
        j++;
    }
}

void f(int i){
    int j, k;
    bool j_binary[NPI/2];
    bool k_binary[NPI/2];

    int imax = fint(exp2(NPI));

    j = fint(random(imax));
    k = fint(random(imax));

    dec_to_bin(j, j_binary);
    dec_to_bin(k, k_binary);

    for(i:int[0,NPI/2 - 1]) {
        bits[i] = j_binary[i];
        bits[i+NPI/2] = k_binary[i];
    }

    //check if this bit combination has already been used
    if(inCoverSet[input]) { inSame = -1*input; nsame++; }
    else inSame = input;
    inCoverSet[bin2dec()] = true;
    covered();

    rCover = 100.0*bitsCovered / NTV; 

    inCoverage = 0.0;
}

